<script>
import BlogAuthor from '@/components/blog/BlogAuthor'

const colors = ['blue', 'orange', 'pink', 'purple']

export default {
  components: { BlogAuthor },
  data() {
    return {
      color: ''
    }
  },
  created() {
    this.color = this.getColor()
  },
  methods: {
    getColor() {
      const color = Math.floor(Math.random() * colors.length)
      return colors[color]
    }
  }
}
</script>

